import type { RefString, TopicId } from '../model/topic';
export type Refs<T> = Record<RefString | TopicId, T>;
export interface Reference<T> {
    refs: Refs<T>;
    fetch: (ref: RefString | TopicId) => T;
}
export declare function makeReference<T>(refs: Refs<T>): Reference<T>;
export declare function mergeReferences<T>(references: ReadonlyArray<Reference<T>>): Reference<T>;
