export function makeReference(refs) {
  return {
    refs,
    fetch(ref) {
      if (typeof this.refs[ref] === "undefined") {
        throw new Error(`Missing Ref "${ref}"`);
      }
      return this.refs[ref];
    }
  };
}
export function mergeReferences(references) {
  const refs = references.reduce((acc, cur) => ({ ...acc, ...cur.refs }), {});
  return makeReference(refs);
}
