import { uuid } from "../common.js";
export class Summary {
  id;
  title;
  from;
  to;
  summaryTopic;
  constructor(title, from, to, summaryTopic) {
    this.id = uuid();
    this.title = title;
    this.from = from;
    this.to = to;
    this.summaryTopic = summaryTopic;
  }
  isConflictWith(SummaryToCompare) {
    return [this.from, this.to].every(
      (identifier) => SummaryToCompare.from === identifier || SummaryToCompare.to === identifier
    );
  }
}
