import path from "path";
export async function writeLocalFile(workbook, pathToFile) {
  const { writeFile } = await import("fs/promises");
  const buffer = await workbook.archive();
  writeFile(pathToFile, Buffer.from(buffer));
}
export async function readImageFile(filePath) {
  const { readFile } = await import("fs/promises");
  const filerBuffer = await readFile(filePath);
  return { data: filerBuffer, name: path.basename(filePath) };
}
